            import ReactDOM from 'react-dom';
            import { Provider } from 'react-redux';
            import App from './App';
            import store from './store'; // Assuming the store is exported from another file

            ReactDOM.render(
                <Provider store={store}>
                    <App />
                </Provider>,
                document.getElementById('root')
            );

